// shouldStopOnFailure returns whether the to stop at the first failure. Default is "True".
// Call with tast run with --var=video.PlatformDecoding.stopOnFailure=false to decode all
// test files regardless of test failure status.
func shouldStopOnFailure(s *testing.State) bool {
	str, ok := s.Var("video.PlatformDecoding.stopOnFailure")
	if !ok {
		return true
	}
	b, err := strconv.ParseBool(str)
	if err != nil {
		s.Fatalf("Cannot parse argument %q to stopOnFailure: %v", str, err)
	}
	if b {
		s.Logf("Runtime variable stopOnFailure is %q. Test case will terminate on the first decode failure", str)
	} else {
		s.Logf("Runtime variable stopOnFailure is %q. Test case will not terminate on the first decode failure", str)
	}
	return b
}

// PlatformDecoding runs the media/gpu/vaapi/test:decode_test binary for vaapi
// or drm-tests/v4l2_stateful_decoder binary for v4l2 stateful or
// media/gpu/v4l2/test:v4l2_stateless_decoder binary for v4l2 stateless
// on the file specified in the testing state.
// The test fails if any of the VAAPI or V4L2 calls fail (or if the test is incorrectly invoked):
// notably, the binary does not check for correctness of decoded output.
// This test is motivated by instances in which libva uprevs may introduce regressions
// and cause decoding to break for reasons unrelated to Chrome.
func PlatformDecoding(ctx context.Context, s *testing.State) {
	testOpt := s.Param().(platformDecodingParams)
	const cleanupTime = 90 * time.Second

	// Reserve time to restart the ui job and perform cleanup at the end of the test.
	cleanupCtx := ctx
	ctx, cancel := ctxutil.Shorten(ctx, cleanupTime)
	defer cancel()

	vl, err := logging.NewVideoLogger()
	if err != nil {
		s.Fatal("Failed to create new video logger: ", err)
	}
	defer vl.Close()

	// Only a single process can have access to the GPU. We do not strictly need
	// to `stop ui` to run the binary, but still do so to shut down the browser
	// and improve benchmarking accuracy.
	// TODO(jchinlee): Move stop ui to a fixture to avoid stopping Chrome all the time once
	// crbug.com/1165694 is resolved.
	if err := upstart.StopJob(ctx, "ui"); err != nil {
		s.Fatal("Failed to stop ui: ", err)
	}
	defer upstart.EnsureJobRunning(cleanupCtx, "ui")

	// Run the decode_test binary for vaapi or the v4l2_stateful_decoder binary or
	// v4l2_stateless_decoder binary for v4l2, propagating its errors: the binary fails
	// if the VAAPI or V4l2 calls themselves error, the binary is called on unsupported
	// inputs or could not open the DRI render node, or the binary otherwise crashes.
	// The test may also fail if the decoded results do not match expected MD5 hashes.
	exec := testOpt.decoder
	stopOnFailure := shouldStopOnFailure(s)

	for _, filename := range testOpt.filenames {
		testing.ContextLogf(ctx, "Running %s on %s", exec, filename)
		args := testOpt.commandBuilder(ctx, s.DataPath(filename))
		stdout, stderr, err := testexec.CommandContext(
			ctx,
			validatePath,
			"--exec="+exec,
			fmt.Sprintf("--args=%s", strings.Join(args, " ")),
			fmt.Sprintf("--metadata=%s.json", s.DataPath(filename)),
		).SeparatedOutput(testexec.DumpLogOnError)

		if err != nil {
			output := append(stdout, stderr...)
			testing.ContextLogf(ctx, "%v failed unexpectedly: %s", exec, string(output))
			errorMessage := fmt.Sprintf("%v failed unexpectedly on %s: ", exec, filename)
			if stopOnFailure {
				s.Fatal(errorMessage, err)
			}
			s.Error(errorMessage, err)
		}
		// TODO(jchinlee): Investigate saving failing frames.
	}
}

// v4l2StatefulDecodeArgs provides the arguments to use with the stateful decoding binary exe for v4l2.
func v4l2StatefulDecodeArgs(ctx context.Context, filename string) (command []string) {
	command = append(command, "--file="+filename, "--md5", "--log_level=1")

	// Query the driver info. If we are on a MediaTek platform, add --mmap to the
	// command line.
	v4l2CtlCmd := testexec.CommandContext(ctx, "v4l2-ctl", "--device",
		"/dev/video-dec0", "-D")
	v4l2Out, err := v4l2CtlCmd.Output(testexec.DumpLogOnError)
	if err != nil {
		return
	}
	mtkDetect := regexp.MustCompile(`mtk-vcodec-dec`)
	if mtkDetect.MatchString(string(v4l2Out)) {
		command = append(command, "--mmap")
	}
	return
}

